package algorithms

import "testing"

func TestLongestPalindrom_GetLength(t *testing.T) {
	type fields struct {
		s string
	}
	tests := []struct {
		name   string
		fields fields
		want   int
	}{
		{name: "Single Character", fields: fields{s: "a"}, want: 1},
		{name: "Mulit character odd length", fields: fields{s: "abccccdd"}, want: 7},
		{name: "Mulit character even length", fields: fields{s: "aabb"}, want: 4},
		{name: "Single character odd length", fields: fields{s: "ccc"}, want: 3},
		{name: "banana", fields: fields{s: "banana"}, want: 5},
		{name: "",
			fields: fields{s: ""},
			want:   983},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			p := LongestPalindrom{
				s: tt.fields.s,
			}
			if got := p.GetLength(); got != tt.want {
				t.Errorf("GetLength() = %v, want %v", got, tt.want)
			}
		})
	}
}
